<script setup>
import { ref, onMounted } from 'vue';
import { useRouter } from 'vue-router';

const router = useRouter();
const supabase = useSupabaseClient();
const tooltipName = ref('');
const tooltipVisible = ref(false);
const tooltipPosition = ref({ x: 0, y: 0 });
const tooltipTimeout = ref(null);
const highlightedCountries = ref(new Set());

const fetchHighlightedCountries = async () => {
  const today = new Date().toISOString().slice(0, 10); 
  const { data, error } = await supabase
    .from('news')
    .select('country')
    .eq('pub_date', today);

  if (error) {
    console.error('Fehler beim Abrufen der News:', error);
    return;
  }

  const countries = new Set(data.map(item => item.country.toLowerCase()));
  console.log('Länder mit Artikeln heute:', countries); 
  highlightedCountries.value = countries; 
  updateCountriesColor(countries);
};


const updateCountriesColor = (countries) => {
  document.querySelectorAll('.cls-1').forEach(path => {
    path.removeEventListener('mouseenter', hoverEnter);
    path.removeEventListener('mouseleave', hoverLeave);
    
    if (countries.has(path.id)) {
      path.style.fill = '#C2E0D2'; 
    } else {
      path.style.fill = ''; 
    }

    path.addEventListener('mouseenter', hoverEnter);
    path.addEventListener('mouseleave', hoverLeave);
  });
};

// Funktion zum Ändern der Füllfarbe beim Hover
const hoverEnter = (event) => {
  if (!event.target.classList.contains('country-selected')) {
    event.target.style.fill = '#68B68B'; 
  }
};

// Funktion zum Zurücksetzen der Füllfarbe beim Verlassen des Hovers
const hoverLeave = (event) => {
  if (!event.target.classList.contains('country-selected')) {
    if (highlightedCountries.value.has(event.target.id)) {
      event.target.style.fill = '#C2E0D2'; 
    } else {
      event.target.style.fill = ''; 
    }
  }
};

const showTooltip = (event, countryId) => {
  
  if (tooltipTimeout.value) {
    clearTimeout(tooltipTimeout.value);
  }
  
  tooltipTimeout.value = setTimeout(() => {
    tooltipPosition.value = { x: event.clientX, y: event.clientY };
    tooltipName.value = countryId;
    tooltipVisible.value = true;
  }, 1000); 
};

const hideTooltip = () => {
  
  if (tooltipTimeout.value) {
    clearTimeout(tooltipTimeout.value);
  }
  tooltipVisible.value = false;
};

const resetCountriesColor = () => {
  const countries = document.querySelectorAll('.cls-1');
  countries.forEach(country => {
    country.style.fill = '';
  });
};

const redirectToCountry = (event) => {
  const countryId = event.target.id;
  if (countryId && countryId !== 'europe') {
    event.target.style.fill = '#E8524B';
    setTimeout(() => {
      router.push(`/${countryId}`);
      setTimeout(() => {
        event.target.style.fill = '';
      }, 300);
    }, 300);
  }
};

onMounted(() => {
  resetCountriesColor();
  fetchHighlightedCountries();
});
</script>




